import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor(props){
    super(props);
    this.state={
      title: 'React Simple CRUD Todo Application',
      act: 0,
      index: '',
      datas: []
    }
  } 

  componentDidMount(){
    this.refs.task.focus();
  }

  fSubmit = (e) =>{
    e.preventDefault();

    let datas = this.state.datas;
    let task = this.refs.task.value;


    if(task) { 
        if(this.state.act === 0){   //new
        let data = {
        task
      }
      datas.push(data);
      }else{                      //update
        let index = this.state.index;
        datas[index].task = task;

      }    

      this.setState({
        datas: datas,
        act: 0
      });

      this.refs.myForm.reset();
      this.refs.task.focus();

    }

  }

  fRemove = (i) => {
    let datas = this.state.datas;
    datas.splice(i,1);
    this.setState({
      datas: datas
    });

    this.refs.myForm.reset();
    this.refs.task.focus();
  }

  fEdit = (i) => {
    let data = this.state.datas[i];
    this.refs.task.value = data.task;


    this.setState({
      act: 1,
      index: i
    });

    this.refs.task.focus();
  }  

  render() {
    let datas = this.state.datas;
    return (
      <div className="App">
        <h2>{this.state.title}</h2>
        <h6>Enter the task in the input box, click on add button to add to the list. To remove from the list, click on the button trash can to be removed.</h6>
        <form ref="myForm" className="">
          <div className="form-group row">
            <label for="formInputLabel" className="col-sm-2 col-form-label">Task</label>
            <input type="text" ref="task" placeholder="your task" className="formField form-control col-sm-9" />
          </div>
          <button onClick={(e)=>this.fSubmit(e)} className="myButton btn btn-success ml-2">submit </button>
        </form>
        <pre>
          {datas.map((data, i) =>
            <li key={i} className="myList">
              {i+1}. {data.task}
              <button onClick={()=>this.fRemove(i)} className="myListButton">remove </button>
              <button onClick={()=>this.fEdit(i)} className="myListButton">edit </button>
            </li>
          )}
        </pre>
      </div>
    );
  }
}

export default App;
