import React from 'react';
import './App.css';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state={
      title: 'React Simle Crud Todo Aplicaton',
      act: 0,
      index: '',
      datas: []
    }
  }

  componentDidMount(){
    this.refs.name.focus();
  }

  fSubmit = (e) =>{
    e.preventDefault();
    console.log('try');

    let datas = this.state.datas;
    let name = this.refs.name.value;
    let address = this.refs.address.value;

    if(this.state.act === 0){ //new
      let data = {
      name, address
      }
      datas.push(data);
    }else{  //update
      let index = this.state.index;
      datas[index].name = name;
      datas[index].address = address;

    }

    this.setState({
      datas: datas,
      act: 0
    })

    this.refs.myForm.reset();
    this.refs.name.focus();
  }

  fRemove = (i) =>{
    let datas = this.state.datas;
    datas.splice(i,1);
    this.setState({
      datas: datas
    });

    this.refs.myForm.reset();
    this.refs.name.focus();
  }

  fEdit = (i) => {
    let data = this.state.datas[i];
    this.refs.name.value=data.name;
    this.refs.address.value=data.address;

    this.setState({
      act: 1,
      index:1
    })

    this.refs.name.focus();

  }

  render() {
    let datas = this.state.datas;

    return (
      <div className="App">
        <h2>{this.state.title}</h2>
        <form ref="myForm" className="myForm">
          <input type="text" ref="name" placeholder="your name" className="formField"/>
          <input type="text" ref="address" placeholder="your address" className="formField"/>
          <button onClick={this.fSubmit} className="myButton">ADD TASK</button>
        </form>
        <pre>
          {datas.map((data, i) => 
            <li key={i} className="myList">
            {i+1}. {data.name}, {data.address} 


          <button onClick={()=>this.fRemove(i)} className="myButton">remove</button>
          <button onClick={()=>this.fEdit(i)} className="myButton">edit</button>
          </li>
          )}
        </pre>
      </div>
    );
  }
}


export default App;
